Utf8CP    GeodeticTransform::GetGroup (Utf8StringR groupName) const
    {
    groupName.clear();

    if (NULL != m_geodeticTransformDef)
        groupName = m_geodeticTransformDef->group;

    return groupName.c_str();
    }

