TEST_F(PolicyTokenTests, GetQualifier_ExistingQualifier_ReturnsQualifier)
    {
    Utf8String cert = "";
    Utf8String token = "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6IkZzMUUwVTA1S1Y4S2V6aVBzVi1iMlk5YWk1WSJ9..C002y5BwwhFQFRAiPg55wnfLzPW6wARCl-0kozCQY_i0NbFA5BBwHGQ5IjiLPYz-2MgevkrcHGFM5duQgQo9734RVQbPkecglE1xb3VeDME0ANTbvpxOL7zqhqGjN-1kZQnP8PtsZxU8mmPbEKncEoaA7U7uJEWFxHvjWeuc4-aYoamatUFqEyq8DQsxdsDzeDndXfDJYY_w1DIn7g-SUoVyBlRaX8LG2rmdz0Rf75IpbSm4vCZT06TNlpAwreZ5Z82pj8Wqi1DLRUd7fQ-CQ12ditEF_qFs9JDFaMWx-SVge2MbStQtGixNqML2LqkMZT454kIEpj642CdQ8DX4XA";

    auto jwToken = JWToken::Create(token, cert);
    EXPECT_NE(jwToken, nullptr);

    auto policyToken = PolicyToken::Create(jwToken);
    EXPECT_NE(policyToken, nullptr);

    auto qualifierJson = policyToken->GetDefaultQualifier("HeartbeatInterval");
    EXPECT_FALSE(qualifierJson.isNull());

    EXPECT_EQ("HeartbeatInterval", qualifierJson["Name"].asString());
    EXPECT_EQ(1, qualifierJson["Value"].asInt());
    }
