bool CS_wktDatumLookUp (const char* datumNameInWkt, char* csDatumName)
{
    bool found = FALSE;    
    int  mapTypeIndex = 7; // Default is Additional
    int  colInd;
    int  rowInd;

    int  numRows = sizeof (DatumMap) / sizeof (DatumMap[0]);
    wchar_t datumNameToFind[DATUM_NAME_LENGTH_LIMIT];
    
    mbstowcs(datumNameToFind, datumNameInWkt, DATUM_NAME_LENGTH_LIMIT);

    
    for (rowInd = 0 ; rowInd < numRows; ++rowInd)
    {        
        if (wcscmp(DatumMap[rowInd][mapTypeIndex], datumNameToFind) == 0)
        {    
            colInd = mapTypeIndex;
            break;
        }
        else
        {        
            for (colInd = 1; colInd < DATUM_MAP_NB_COLS; colInd++)
            {
                if (colInd == mapTypeIndex) continue;

                if (wcscmp(DatumMap[rowInd][colInd], datumNameToFind) == 0)
                {            
                    break;
                }
            }

            //The datum name has been found in the map.
            if (colInd < DATUM_MAP_NB_COLS)
                break;
        }
    }

    if (rowInd < numRows)
    {
        // If the CSMAP value is empty ... we use the EPSG value
        if (wcscmp(DatumMap[rowInd][0], L"") == 0) 
            wcstombs(csDatumName, DatumMap[rowInd][5], DATUM_NAME_LENGTH_LIMIT);
        else
            wcstombs(csDatumName, DatumMap[rowInd][0], DATUM_NAME_LENGTH_LIMIT);
        found = TRUE;
    }

    return found;
}